import React, { useState, useEffect } from 'react';

import UserProfile from '../../containers/UserProfile';
import UserPosts from '../../containers/UserPosts';

import Loading from '../../components/Loading';

const ProfileRoute = () => {
  const [user, setUser] = useState({})
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    const { pathname } = window.location;
    const name = pathname.split("/user/")[1];

    fetch(`https://5e7d0266a917d70016684219.mockapi.io/api/v1/users?search=${name}`)
    .then(res => res.json())
    .then(([user]) => setUser(user));
  }, []);

  return (
    <div data-testid="profile-route-route">
      {
        user ? <UserProfile avatar={user.avatar} name={user.name} username={user.username}/> : <Loading />
      }

      <UserPosts />
    </div>
  );
};

export default ProfileRoute;
