import React, { useState, useEffect } from 'react';

import Stories from '../../containers/Stories';
import Loading from '../../components/Loading';

import Posts from '../../containers/Posts';

import './FeedRoute.scss';

const FeedRoute = () => {
  const [ posts, setPosts ] = useState([]);
  const [ users, setUsers ] = useState([]);


  // TODO Get all users' stories (create a state variable)


  function getUserHandler(id) {
    return users.find((user) => user.id === id);
  }


  function getUsers() {
    fetch('https://5e7d0266a917d70016684219.mockapi.io/api/v1/users', { method: 'GET'})
    .then((response) => response.json())
    .then((users) => {
      setUsers(users);
      users.forEach((user) => {
        getAllPostsByUser(user.id)
      })
    });
  }

  function getAllPostsByUser(id) {
    fetch(`https://5e7d0266a917d70016684219.mockapi.io/api/v1/users/${id}/posts`, 
    { method:'GET' })
    .then( async (response) => {
      const data = await response.json();
      setPosts((prev) => {
        return [...prev, ...data];
      });
      console.log(data);
    })
  }

  useEffect(() => {
    getUsers();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="feed" data-testid="feed-route">
      {
        <Posts posts={posts} getUserHandler={getUserHandler}/>

      }
    </div>
  );
};

export default FeedRoute;
