import React, { useState, useEffect } from 'react';

import Stories from '../../containers/Stories';
import Loading from '../../components/Loading';

import Posts from '../../containers/Posts';

import './FeedRoute.scss';

const FeedRoute = () => {
  const [ posts, setPosts] = useState([]);
  // TODO Get all users ( create a state  variable)
  // TODO Get all posts based in all users (create a state variable)
    // TODO Create a function that receives a user id and uses that to fetch post's api
  // TODO Get all users' stories (create a state variable)
  // TODO Get information about loading
  // TODO Create a function that returns a user based on their id
  function getUsers() {
    fetch('https://5e7d0266a917d70016684219.mockapi.io/api/v1/users', { method: 'GET'})
    .then((response) => response.json);

  }

  function getAllPostsByUser(id) {
    fetch(`https://5e7d0266a917d70016684219.mockapi.io/api/v1/users/${id}/posts`, 
    { method:'GET' })
    .then( async (response) => {
      const data = await response.json();
      setPosts(data);
    })
  }

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div className="feed" data-testid="feed-route">
      <Posts posts={posts} />
    </div>
  );
};

export default FeedRoute;
