import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import './Post.scss';

const Post = ({ postInfo, userInfo }) => {
  // useState

  // desestruturar objetos que vai usar

  return (
    <article className="post" data-testid="post">
      <header className="post__header">
        <div className="user">
          <img className="user__thumb" src={userInfo.avatar} alt={`Foto do ${userInfo.name}`} />
          <span className>{userInfo.name}</span>
        </div>
        <button className="post__content">
          <span className="follow-btn">Seguir </span>
          {/* Quando clicar  &.is-following  */}
        </button>
      </header>

      <figure className="post__figure">
        <img src={postInfo.imageUrl} alt="" />
      </figure>

      <div className="post_controls">
        <i class="fas fa-heart post__control" aria-hidden="true"></i>
      </div>

      <div className="post__status">
        <div className="user">
          {/* <span/>
          <img className="user__thumb" src={} alt={} /> */}
        </div>
      </div>
    </article>
  );
};

export default Post;



